package codecrafter47.bungeetablistplus.managers;

import codecrafter47.bungeetablistplus.api.bungee.Icon;

import javax.annotation.Nullable;
import java.awt.image.BufferedImage;
import java.util.function.Consumer;

public interface SkinManager {
    Icon defaultSkin = new Icon(null, new String[0][]);
    Icon missingSkinTexture = new Icon(null, new String[][]{{"textures", "", ""}});
    Icon loading0 = new Icon(null, new String[][]{{"textures", "", ""}});
    Icon loading1 = new Icon(null, new String[][]{{"textures", "", ""}});
    Icon loading2 = new Icon(null, new String[][]{{"textures", "", ""}});
    Icon loading3 = new Icon(null, new String[][]{{"textures", "", ""}});

    @Nullable
    Icon getIcon(String nameOrUUID);

    void onReload();

    void createIcon(BufferedImage image, Consumer<Icon> callback);
}
